from  pywebio import start_server
from pywebio.input import *
from pywebio.output import *
from pywebio.session import *
from pywebio.pin import *

def Rak ():
    put_html('<center><h3>Aplication For Accepted Student</h3></center>').style('background-color:#7FBCD2; color:gold; padding:25px;')
    put_html('<center><p>web form for accepted students CV</p></center>').style(' color:red; font-size:24px; font-weight:bold;')
    put_html('<center><img src="data:image/jpeg;base64,"></center>')
    data = input_group(
        'accepted Students Application fill form',
        [input('student name',name='student'),
         input('student Address',name='country'),
         input('Email address',name='email'),
         input('phone number',name='phone'),
         radio('student hobbies',options = ['word','excel','ppt'],name='certi'),
         checkbox('languages',options = ['arabic','eng','french'],inline=True,name ='lang')
         ],
    )
    imgs = file_upload(
        'download pic',
        accept = 'image/*',
        multiple = True
    )
    for img in imgs:
      global rr 
      rr = img ['content']
    put_text('student cv: ')
    #https://youtu.be/CUFjAS1qvbU?list=PLSiLeKadTQ7n8wFp1e1dKdtCYTy6DZTAL&t=1812
    put_table([
        ['student Img','name','address','phone','certi','lang'],
        [put_image (rr).style('width:50px;'),data['student'],data['country'],data['phone'],data['certi'],data['lang']
    ]])
    
start_server(Rak,port=5000,debug =True)